import styled from 'styled-components'
import ArrowLeftOutlinedIcon from '@material-ui/icons/ArrowLeftOutlined';
import ArrowRightOutlinedIcon from '@material-ui/icons/ArrowRightOutlined';

const Container = styled.div`

        height:100vh;
        width:100%;
        display:flex;
        
        position:relative;
        overflow:hidden;

`
const Arrow = styled.div`

        height:50px;
        width:50px;
        background-color: #fff7f7;
        border-radius:50%;
        display:flex;
        align-items:center;
        justify-content:center;
        position:absolute;

        top:0;
        bottom:0;
        margin:auto;

        left: ${props => props.direction === 'left' && "10px"};
        right: ${props => props.direction === 'right' && "10px"};
        cursor:pointer;
        opacity:0.6;
        z-index:2;
`

const Wrapper = styled.div`

        height:100%;
        display:flex;
        transform:translateX(0vw);
        
`
const Slide = styled.div`

    display:flex;
    align-items:center;
    height:100vh;
    width:100vw;

    background-color: ${props => props.bgcolor ==='f5fafd' && ''};
    background-color: ${props => props.bgcolor === 'fcf1ed' && ''};
    background-color: ${props => props.bgcolor ==='fcf1ed' && ''};
`
const ImageContainer = styled.div`
        flex:1;
        height:100%;
`
const Image = styled.img`

        height:80%;        
`
const InfoContainer = styled.div`

        flex:1;
        padding:50px;

`
const Title = styled.h1`

        font-size:70px;
`
const Desc = styled.p`

        margin:50px 0px;
        font-size:20px;
        font-weight:500;
        letter-spacing:3px;
`
const Button = styled.button`

    padding:10px;
    font-size:20px;
    background-color:transparent;
    cursor:pointer;

    `

const Slider = () => {
    return (
        <Container>
            <Arrow direction='left' onClick={()=>handleClick('left')}>
                <ArrowLeftOutlinedIcon />
            </Arrow>
            <Wrapper>
                <Slide bgcolor='f5fafd'>
                    <ImageContainer>
                        <Image src='https://i.ibb.co/XsdmR2c/1.png'/>
                    </ImageContainer>
                    <InfoContainer>
                        <Title>Summer Sale!!!</Title>
                        <Desc>Dont Waste Your Time and Avail New Arrivals with 50% Off</Desc>
                        <Button>Shop NOW!!!!</Button>
                    </InfoContainer>
                </Slide>
                <Slide bgcolor='fcf1ed'>
                    <ImageContainer>
                        <Image src='https://i.ibb.co/XsdmR2c/1.png'/>
                    </ImageContainer>
                    <InfoContainer>
                        <Title>Winter Sale!!!</Title>
                        <Desc>Dont Waste Your Time and Avail New Arrivals with 50% Off</Desc>
                        <Button>Shop NOW!!!!</Button>
                    </InfoContainer>
                </Slide>
                <Slide bgcolor='f5fafd'>
                    <ImageContainer>
                        <Image src='https://i.ibb.co/XsdmR2c/1.png'/>
                    </ImageContainer>
                    <InfoContainer>
                        <Title>Popular Sale!!!</Title>
                        <Desc>Dont Waste Your Time and Avail New Arrivals with 50% Off</Desc>
                        <Button>Shop NOW!!!!</Button>
                    </InfoContainer>
                </Slide>
            </Wrapper>
            <Arrow direction='right' onClick={()=> handleClick('right')}>
                <ArrowRightOutlinedIcon/>
            </Arrow>
        </Container>
    )
}

export default Slider;
